var pc = require('ProcessChain');
var allJobs = [];

function End(write, end)
{
    this.write = write;
    this.exec = function() { throw "Does this happen?"; };
    this.end = end;
}

function JavaScript(func)
{
    if (typeof func === "function")
        this.exec = func;
    this._next = undefined;
    this._data = undefined;
    this._written = false;
    this._input = "";
    this._inputPos = 0;
    this._done = false;
}

// Should be overridden
JavaScript.prototype.exec = function(data)
{
    return data;
};

JavaScript.prototype.chain = function(js)
{
    var obj = this;
    while (obj._next !== undefined)
        obj = obj._next;
    obj._next = js;
};

// function* foo()
// {
//     var sum = 0;
//     var data;
//     while ((data = yield undefined) !== undefined)
//         sum += parseInt(data);
//     return sum;
// }

JavaScript.prototype.write = function(data)
{
    if (this._done)
        return;
    var that = this;
    this._input += data;
    if (!this._iterator)
        this._iterator = this.exec(data);

    while (this._inputPos < this._input.length) {
        var idx = this._input.indexOf(jsh.IFS, this._inputPos);
        if (idx === -1)
            break;
        var chunk = this._input.substring(this._inputPos, idx);
        this._inputPos = idx;
        var out = this._iterator.next(chunk);
        if (out.value) {
            if (this._next) {
                this._next.write(out.value);
            } else if (!this._data) {
                this._data = out.value;
            } else {
                this._data += out.value;
            }
        }
        if (out.done) {
            this._done = true;
            break;
        }
    }
    this._written = true;
};

JavaScript.prototype.end = function(callback)
{
    var obj = this;

    if (!this._written)
        this.write();

    while (obj._next !== undefined)
        obj = obj._next;
    callback(obj._data);
    obj._data = undefined;
};

JavaScript.prototype.isLast = function()
{
    return (this._next === undefined);
};

function Job()
{
    this._jobs = [];
    this._chains = [];
    this._currentJob = undefined;
    this._iterator = undefined;
}

Job.prototype.toString = function()
{
    if (this._currentJob === undefined) {
        throw "Job not started yet, this shouldn't happen";
    }
    if (this._currentJob < 0 || this._currentJob >= this._jobs.length) {
        throw "_currentJob out of range, " + this._currentJob + " vs " + this._jobs.length;
    }

    var sub = this._jobs[this._currentJob];
    if (sub.type === "process") {
        return sub.jobs[0].program;
    } else if (sub.type === "js") {
        return JSON.stringify(sub.job);
    } else {
        throw "Unknown job type " + sub.type;
    }
};

Job.prototype.cont = function(type)
{
    if (this._currentJob === undefined) {
        throw "Job not started yet, this shouldn't happen";
    }
    if (this._currentJob < 0 || this._currentJob >= this._jobs.length) {
        throw "_currentJob out of range, " + this._currentJob + " vs " + this._jobs.length;
    }

    var sub = this._jobs[this._currentJob];
    if (sub.type === "process") {
        if (sub._pchain === undefined) {
            throw "_pchain is undefined";
        }
        this.type = type;
        this.status = 0; // 0 = RUNNING
        sub._pchain.cont(type);
    } else {
        throw "Can't continue JavaScript jobs";
    }
};

Job.prototype.process = function(process)
{
    // try to find the program
    if (typeof process.program !== "string") {
        throw "Undefined program";
    }
    process.program = jsh.pathify(process.program);

    var idx = this._jobs.length;
    if (this._jobs.length === 0 || this._jobs[idx - 1].type !== "process") {
        var p = { type: "process", pchain: new pc.ProcessChain(jsh.jshNative) };
        p.pchain.chain(process);
        this._jobs.push(p);
    } else {
        this._jobs[idx - 1].pchain.chain(process);
    }
    return this;
};

Job.prototype.proc = Job.prototype.process;

Job.prototype.js = function(js)
{
    if (!(js instanceof JavaScript))
        return this;

    this._jobs.push({type: "js", script: js});
    return this;
};

Job.prototype.exec = function(type, outCallback, doneCallback)
{
    // set job status to RUNNING
    this.status = 0;
    // add to list of jobs
    allJobs.push(this);
    this._jobs.push(new End(outCallback, doneCallback));
    // go!
    this._iterator = this._runChain(type);
};

Job.prototype._runChain = function*(type) {
    for (var i = 0; i < this._jobs.length - 1; ++i) {
        var job = this._jobs[i];
        job._next = this._jobs[i + 1];
    }
    this._chains.push({write: outCallback, type: "end"});

};

Job.prototype.cleanup = function()
{
    var sub = this._jobs[this._currentJob];
    if (sub.type === "process") {
        if (sub._pchain === undefined) {
            throw "_pchain is undefined";
        }
        sub._pchain.cleanup();
    }
};

Job.prototype._update = function(status)
{
    if (status === 2) { // TERMINATED
        var idx = allJobs.indexOf(this);
        if (idx === -1) {
            throw "Job not in allJobs";
        }
        allJobs.splice(idx, 1);
        this.status = undefined;
    } else {
        this.status = status;
    }
};

Job.prototype._runJobs = function(type, doneCallback)
{
    this.type = type;
    var that = this;
    var pchain, jchain, lastJob;
    for (var i = 0; i < this._jobs.length; ++i) {
        this._currentJob = i;
        lastJob = (i == this._jobs.length - 1);
        var job = this._jobs[i];
        if (job.type === "process") {
            pchain = new pc.ProcessChain(jsh.jshNative, type);
            job._pchain = pchain;
            for (var j in job.processes) {
                pchain.chain(job.jobs[j]);
            }
            if (jchain) {
                jchain.end(function(data) { pchain.write(data); });
                jchain = undefined;
            }
        } else {
            jchain = job.job;
            if (pchain) {
                // exec process, wait until complete before continuing chain
                pchain.exec(function(data) {
                    if (data.type === "stdout") {
                        jchain.write(data.data);
                    } else {
                        if (!lastJob) { // complete, continue chain now
                            that._runJobsFrom(i + 1, type, outCallback, doneCallback, jchain);
                        } else {
                            jchain.end(outCallback);
                            if (doneCallback) {
                                that._update(data.status);
                                doneCallback(data.code);
                            }
                        }
                    }
                });
                return;
            }
        }
    }
    if (jchain) {
        jchain.end(outCallback);
        if (doneCallback) {
            that._update(2); // 2 = TERMINATED
            // ### Final JS chain always returns success?
            doneCallback(0);
        }
    } else if (pchain) {
        pchain.exec(function(data) {
            if (data.type === "stdout") {
                outCallback(data.data);
            } else { // complete, tell JS we're done
                if (doneCallback) {
                    that._update(data.status);
                    doneCallback(data.code);
                }
            }
        });
    }
};

function cleanup()
{
    for (var idx = 0; idx < allJobs.length; ++idx) {
        allJobs[idx].cleanup();
    }
}

module.exports = {
    Job: Job,
    Jobs: allJobs,
    JavaScript: JavaScript,
    cleanup: cleanup,
    FOREGROUND: 0,
    BACKGROUND: 1,
    RUNNING: 0,
    STOPPED: 1,
    TERMINATED: 2
};
